import React, { Component,Fragment } from 'react';

class Addmessage extends Component{
    constructor(props){
        super(props)
    }
    render(){
        return(
            <Fragment>
                <div>
                    新增信息
                </div>
                <div>
                <div className="app">
                    <input 
                    type="text" 
                    placeholder="姓名" 
                    value={this.state.name}
                    onChange={this.nameVal}
                    /><br/>
                    <input 
                    type="text" 
                    placeholder="性别" 
                    value={this.state.sex}
                    onChange={this.sexVal}          
                    /><br/>
                    <input 
                    type="number" 
                    placeholder="年龄" 
                    value={this.state.age}
                    onChange={this.ageVal}          
                    /><br/>
                    <input 
                    type="text" 
                    placeholder="爱好" 
                    value={this.state.hobby}
                    onChange={this.hobbyVal}          
                    /><br/>
                    <button onClick={this.submit}>提交</button>
                    <button onClick={this.show}>显示</button>
                    <br/>
                </div>
                <div id="info">{
                    // 未分离写法
                    this.state.list.map((item,index)=>{
                        return ( 
                        <div key={item}>
                            {/* <p key={item}>
                            <h3>姓名：{item.name}</h3>
                            <h3>性别：{item.sex}</h3>
                            </p> */}
                            <Infolist 
                            name={item.name}
                            sex={item.sex}
                            age={item.age}
                            hobby={item.hobby}
                            />
                        </div>
                        )
                    })
                    }
                    </div>
                </div>
            </Fragment>

        )
    }
}

export default Addmessage;
