import React, { Component,Fragment } from 'react';
import HomePage from './homepage';

var arr = [];
class Addmessage extends Component{
    constructor(props){
        super(props);
        this.state = {
          name:'',
          sex:'',
          age:'',
          hobby:'',
          list:[]
        }
        this.submit = this.submit.bind(this);
        this.nameVal = this.nameVal.bind(this);
        this.sexVal = this.sexVal.bind(this);
        this.ageVal = this.ageVal.bind(this);
        this.hobbyVal = this.hobbyVal.bind(this);
        this.show = this.show.bind(this);
    }
    nameVal(e){
        this.setState({
          name:e.target.value
        })
      }
      sexVal(e){
        this.setState({
          sex:e.target.value
        })
      }
      ageVal(e){
        this.setState({
          age:e.target.value
        })
      }
      hobbyVal(e){
        this.setState({
          hobby:e.target.value
        })
      }
      submit(){
        if(this.state.name !== '' && this.state.sex !== ''){
          arr.push({
            name:this.state.name,
            sex:this.state.sex,
            age:this.state.age,
            hobby:this.state.hobby
          })
          // console.log(arr);
          this.setState({
            list:arr,
          })
          this.state.name = '';
          this.state.sex = '';
          this.state.age = '';
          this.state.hobby = '';
          // console.log(this.state.list);
          console.log('提交成功');
        }else{
          console.log('未输入信息');
        }
      }
      show(){
        console.log(this.state.list);
      }
    render(){
        return(
            <Fragment>
                <div>
                    新增信息
                </div>
                <div>
                <div className="app">
                    <input 
                    type="text" 
                    placeholder="姓名" 
                    value={this.state.name}
                    onChange={this.nameVal}
                    /><br/>
                    <input 
                    type="text" 
                    placeholder="性别" 
                    value={this.state.sex}
                    onChange={this.sexVal}          
                    /><br/>
                    <input 
                    type="number" 
                    placeholder="年龄" 
                    value={this.state.age}
                    onChange={this.ageVal}          
                    /><br/>
                    <input 
                    type="text" 
                    placeholder="爱好" 
                    value={this.state.hobby}
                    onChange={this.hobbyVal}          
                    /><br/>
                    <button onClick={this.submit}>提交</button>
                    <button onClick={this.show}>显示</button>
                    <br/>
                </div>
                <div id="info">{
                    // 未分离写法
                    this.state.list.map((item,index)=>{
                        return ( 
                        <div key={item}>
                            {/* <p key={item}>
                            <h3>姓名：{item.name}</h3>
                            <h3>性别：{item.sex}</h3>
                            </p> */}
                            <HomePage 
                            name={item.name}
                            sex={item.sex}
                            age={item.age}
                            hobby={item.hobby}
                            />
                        </div>
                        )
                    })
                    }
                    </div>
                </div>
            </Fragment>

        )
    }
}

export default Addmessage;
