import {GET_INIT_LIST} from "./actionTypes";
import {takeEvery} from "redux-saga/effects";
//generator函数
function* getInitList(){
    console.log(123)
}
function* mySage(){
    //第一个参数为action的类型
    yield takeEvery(GET_INIT_LIST, getInitList)
}
export default mySage;
